import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

function BookNow() {
  const { movieId } = useParams(); // Get the movie ID from the URL
  const [movie, setMovie] = useState(null);
  const user = sessionStorage.getItem('username'); // Assume the user is always logged in

  useEffect(() => {
    // Fetch movie details using the movieId
    fetch(`http://localhost:5001/movies/${movieId}`)
      .then((response) => response.json())
      .then((data) => setMovie(data))
      .catch((error) => console.error('Error fetching movie:', error));
  }, [movieId]);

  if (!movie) {
    return <p>Loading movie details...</p>;
  }

  return (
    console.log(user),
    <div className="book-now-page">
      <h1>Welcome to the Book Now page!</h1>
      <h3>Hi, {user}!</h3> {/* Display the logged-in user's name */}
      <p>You are booking tickets for <strong>{movie.movie_name}</strong></p> {/* Display the movie name */}
      <p>Proceed with your booking here.</p>
      {/* Add additional booking details or a booking form if needed */}
    </div>
  );
}

export default BookNow;
