import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';

// Import components for your pages
import MovieDetails from './pages/MovieDetails';
import LandingPage from './pages/LandingPage';
import Login from './pages/Login';
import Register from './pages/Register';
// import BookNow from './pages/BookNow';  


function App() {
  const [user, setUser] = useState(null); // State to store logged-in user data

  const handleLoginSuccess = (loggedInUser) => {
    setUser(loggedInUser); // Set the logged-in user data
    console.log('User logged in:', loggedInUser); 
  };

  const handleLogout = () => {
    setUser(null); // Clear the user data
    localStorage.removeItem('token'); // Remove the token from local storage
  };

  return (
    <Router>
      <div className="App">
        <Routes>
          <Route path="/" element={<LandingPage />} />
          <Route path="/" element={<LandingPage user={user} handleLogout={handleLogout} />} />
          <Route path="/login" element={<Login onLoginSuccess={handleLoginSuccess} />} />
          <Route path="/register" element={<Register />} />
          <Route path="/movie/:id" element={<MovieDetails />} />
          {/* <Route path="/book-now" element={<BookNow />} /> */}
        </Routes>
      </div>
    </Router>
  );
}

export default App;
