import React, { useState, useEffect } from 'react';

function Movie() {
  const [movies, setMovies] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch movie data from the backend
  useEffect(() => {
    fetch('http://localhost:5001/movies')  // Fetch from the backend (update URL to /movies)
      .then((response) => response.json())
      .then((data) => {
        setMovies(data);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching movies:', error);
        setLoading(false);
      });
  }, []);

  if (loading) {
    return <p>Loading movies...</p>;
  }

  return (
    <div className="movie-container">
      {movies.length === 0 ? (
        <p>No movies available.</p>
      ) : (
        movies.map((movie) => (
          <div className="movie-card" key={movie._id}>
            <img src={movie.image} alt={movie.movie_name} className="movie-image" />
            <div className="movie-details">
              <h3 className="movie-title">{movie.movie_name}</h3>
              <p className="movie-date">Release Date: {movie.release_date}</p>
              <p className="movie-genre">Genre: {movie.genre}</p>
              <p className="movie-synopsis">{movie.synopsis}</p>
            </div>
          </div>
        ))
      )}
    </div>
  );
}

export default Movie;
