import React from 'react';
import { useNavigate } from 'react-router-dom';

function LandingPage({ user, handleLogout }) {
  const navigate = useNavigate();

  return (
    <div className="landing-page">
      <header>
        <h1>Welcome to the Movie App!</h1>
        <div className="header-buttons">
          {user ? (
            <>
              {/* Show Welcome and Logout Button if User is Logged In */}
              <span>Welcome, {user.username}!</span>
              <button
                onClick={() => {
                  handleLogout(); // Clear user state and token
                }}
              >
                Logout
              </button>
            </>
          ) : (
            <>
              {/* Show Login Button if No User is Logged In */}
              <button onClick={() => navigate('/login')}>Login</button>
            </>
          )}
        </div>
      </header>
      <main>
        <p>Explore the latest movies and book tickets easily.</p>
      </main>
    </div>
  );
}

export default LandingPage;
