import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../styles/bookNow.css';

function BookNow({ user }) {
  const { movieId } = useParams(); // Get the movie ID from the URL
  const navigate = useNavigate(); // To navigate to another page if needed
  const [movie, setMovie] = useState(null);
  const [cinemas, setCinemas] = useState([]);
  const [selectedCinema, setSelectedCinema] = useState(null);
  const [selectedShowtime, setSelectedShowtime] = useState(null);
  const [error, setError] = useState(null);
  const [showModal, setShowModal] = useState(false); // State to show/hide the modal
  const [bookingConfirmed, setBookingConfirmed] = useState(false); // State for booking confirmation message

  // Fetch movie details
  useEffect(() => {
    fetch(`http://localhost:5001/movies/${movieId}`)
      .then((response) => response.json())
      .then((data) => setMovie(data))
      .catch((error) => setError(error.message));
  }, [movieId]);

  // Fetch cinemas for the movieId
  useEffect(() => {
    fetch(`http://localhost:5001/cinemas/movie/${movieId}`)
      .then((response) => response.json())
      .then((data) => {
        if (data.message && data.message === "No cinemas found for this movie") {
          setError("No cinemas found for this movie.");
        } else {
          setCinemas(data);
          setError(null); // Clear any previous errors if cinemas are found
        }
      })
      .catch((error) => setError(error.message));
  }, [movieId]);

  // Handle cinema selection
  const handleCinemaSelect = (cinemaId) => {
    setSelectedCinema(cinemaId);
    setSelectedShowtime(null); // Reset showtime selection when a new cinema is selected
  };

  // Handle showtime selection
  const handleShowtimeSelect = (showtime) => {
    setSelectedShowtime(showtime);
    setBookingConfirmed(false); // Reset booking confirmation message
  };

  // Handle booking confirmation
  const handleConfirmBooking = () => {
    setBookingConfirmed(true); // Set the booking as confirmed
  };

  // Check if movie is available
  if (!movie) {
    return <p>Loading movie details...</p>;
  }

  return (
    <div className="book-now-page">
      <header>
        <h1>Book Your Movie Ticket</h1>
        <h3>Hi, {user ? user.name : 'Guest'}!</h3>
        <h4>Movie: {movie.movie_name}</h4>
      </header>

      {/* Error message */}
      {error && (
        <p style={{ fontWeight: 'bold', color: 'red' }}>
          {error}
        </p>
      )}

      {/* Cinema Selection */}
      {cinemas.length === 0 && !error && (
        <div className="no-cinemas">
          <p style={{ fontWeight: 'bold', color: 'red' }}>
            No cinemas available for booking for this movie.
          </p>
        </div>
      )}

      {cinemas.length > 0 && !error && (
        <div className="cinema-selection">
          <h2>Select Your Cinema</h2>
          <div className="cinema-list">
            {cinemas.map((cinema) => (
              <div
                key={cinema._id}
                className={`cinema-item ${selectedCinema === cinema._id ? 'selected' : ''}`}
                onClick={() => handleCinemaSelect(cinema._id)}
              >
                <h3>{cinema.cinema_name}</h3>
                {selectedCinema === cinema._id && (
                  <div className="showtimes">
                    <h4>Available Showtimes:</h4>
                    {cinema.showtimes.length === 0 ? (
                      <p>No showtimes available.</p>
                    ) : (
                      cinema.showtimes.map((showtime, index) => (
                        <button
                          key={index}
                          className={`showtime-btn ${selectedShowtime === showtime ? 'selected' : ''}`}
                          onClick={() => handleShowtimeSelect(showtime)}
                        >
                          {showtime.showtime} - {new Date(showtime.date).toLocaleDateString()}
                        </button>
                      ))
                    )}
                  </div>
                )}
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Booking Summary */}
      {selectedShowtime && selectedCinema && !bookingConfirmed && (
        <section className="booking-summary">
          <h3>Booking Summary</h3>
          <p>Cinema: {cinemas.find((cinema) => cinema._id === selectedCinema)?.cinema_name}</p>
          <p>
            Showtime: {selectedShowtime.showtime} on {new Date(selectedShowtime.date).toLocaleString()}
          </p>
          <button className="confirm-btn" onClick={handleConfirmBooking} disabled={!selectedCinema || !selectedShowtime}>
            Confirm Booking
          </button>
        </section>
      )}

      {/* Booking Confirmed Message */}
      {bookingConfirmed && (
        <section className="booking-confirmed-message">
          <h3>Booking Confirmed!</h3>
          <p>Your booking has been successfully confirmed for:</p>
          <p><strong>Movie:</strong> {movie.movie_name}</p>
          <p><strong>Cinema:</strong> {cinemas.find(cinema => cinema._id === selectedCinema)?.cinema_name}</p>
          <p><strong>Showtime:</strong> {selectedShowtime.showtime} on {new Date(selectedShowtime.date).toLocaleString()}</p>
        </section>
      )}
    </div>
  );
}

export default BookNow;
