import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom'; // Import Link and useNavigate for navigation

function Movie() {
  const [movies, setMovies] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchQuery, setSearchQuery] = useState('');
  const navigate = useNavigate(); // Use navigate for redirection

  // Fetch movie data from the backend
  useEffect(() => {
    fetch('http://localhost:5001/movies')  // Fetch from the backend
      .then((response) => response.json())
      .then((data) => {
        setMovies(data);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching movies:', error);
        setLoading(false);
      });
  }, []);

  if (loading) {
    return <p>Loading movies...</p>;
  }

  // Search movies by title
  const filteredMovies = movies.filter((movie) =>
    movie.movie_name.toLowerCase().includes(searchQuery.toLowerCase())
  );

  // Handle Book Now click, check login status
  const handleBookNow = (movieId) => {
    const token = localStorage.getItem('token'); // Check if user is logged in (via token)

    if (token) {
      navigate(`/book-now/${movieId}`); // Redirect to booking page if logged in
    } else {
      navigate('/login'); // Redirect to login page if not logged in
    }
  };

  return (
    <div className="movie-container">
      <header className="movie-header">
        <h1>Movie Booking App</h1>
        <div className="header-right">
          {/* Login Button */}
          <Link to="/login" className="btn-login">Login</Link>
          {/* Search Bar */}
          <input
            type="text"
            placeholder="Search movies..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="search-bar"
          />
        </div>
      </header>

      {/* Movie Details */}
      <div className="movie-cards">
        {filteredMovies.length === 0 ? (
          <p>No movies available.</p>
        ) : (
          filteredMovies.map((movie) => (
            <div className="movie-card" key={movie._id}>
              <img src={movie.image} alt={movie.movie_name} className="movie-image" />
              <div className="movie-info">
                <h3>{movie.movie_name}</h3>
                <p>{movie.synopsis}</p>
                <div className="movie-buttons">
                  {/* View Details button */}
                  <Link to={`/movie/${movie._id}`} className="btn-view-details">View Details</Link>
                  {/* Book Now button */}
                  <button className="btn-book-now" onClick={() => handleBookNow(movie._id)}>
                    Book Now
                  </button>
                </div>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

export default Movie;
