import React from 'react';
import { Link } from 'react-router-dom';
import Movie from './Movie';  // Reusing your Movie component


function LandingPage() {
  return (
    <div className="landing-page">
      <header>
        <h1>Welcome to the Movie App!</h1>
        <div className="header-buttons">
          {user ? (
            <>
              {/* Show Welcome and Logout Button if User is Logged In */}
              <span>Welcome, {user.username}!</span>
              <button
                onClick={() => {
                  handleLogout(); // Clear user state and token
                }}
              >
                Logout
              </button>
            </>
          ) : (
            <>
              {/* Show Login Button if No User is Logged In */}
              <button onClick={() => navigate('/login')}>Login</button>
            </>
          )}
        </div>
      </header>

      <section className="movie-list">
        <h2 className="movie-list-heading">Featured Movies</h2>
        <Movie /> {/* Embed the Movie component here */}
      </section>
    </div>
  );
}

export default LandingPage;
