import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

function BookNowPage() {
  const { id } = useParams(); // Get movie ID from URL
  const [movie, setMovie] = useState(null);
  const [user, setUser] = useState(null);

  useEffect(() => {
    // Fetch movie details using the movie ID
    fetch(`http://localhost:5001/movies/${id}`)
      .then((response) => response.json())
      .then((data) => setMovie(data))
      .catch((error) => console.error('Error fetching movie:', error));

    // Fetch user details from sessionStorage
    const loggedInUser = sessionStorage.getItem('username');
    if (loggedInUser) {
      setUser(loggedInUser); // Set user if available in sessionStorage
    }
  }, [id]);

  if (!movie) {
    return <p>Loading movie details...</p>;
  }

  return (
    <div className="book-now-page">
      <h2>Welcome, {user}!</h2>
      <h3>Book Your Movie: {movie.movie_name}</h3>

      <div className="movie-details">
        <img src={movie.image} alt={movie.movie_name} />
        <div className="movie-info">
          <p><strong>Genre:</strong> {movie.genre}</p>
          <p><strong>Duration:</strong> {movie.duration} minutes</p>
          <p><strong>Synopsis:</strong> {movie.synopsis}</p>
          {/* Add other details you want to display */}
        </div>
      </div>

      <button className="btn-book-now">Confirm Booking</button>
    </div>
  );
}

export default BookNowPage;
