import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';

// Import components for your pages
import MovieDetails from './pages/MovieDetails';
import LandingPage from './pages/LandingPage';
import Login from './pages/Login';
import Register from './pages/Register';
// import BookNow from './pages/BookNow';  // Uncomment when you're ready for the BookNow page

// Import your styles
// import './styles/movie.css';

function App() {
  const [user, setUser] = useState(null); // State to store logged-in user data

  const handleLoginSuccess = (loggedInUser) => {
    setUser(loggedInUser); // Set the logged-in user data
    console.log('User logged in:', loggedInUser); // Debugging: log the user data
  };

  return (
    <Router>
      <div className="App">
        <Routes>
          {/* Define the route for the landing page */}
          <Route path="/" element={<LandingPage />} />

          {/* Define the route for the login page */}
          <Route path="/login" element={<Login onLoginSuccess={handleLoginSuccess} />} />

          {/* Define the route for the register page */}
          <Route path="/register" element={<Register />} />

          {/* Define the route for movie details page */}
          <Route path="/movie/:id" element={<MovieDetails />} />

          {/* Uncomment the following line when ready to implement the book-now page */}
          {/* <Route path="/book-now" element={<BookNow />} /> */}
        </Routes>
      </div>
    </Router>
  );
}

export default App;
