import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

function BookNow() {
  const { movieId } = useParams(); // Get the movie ID from the URL
  const [movie, setMovie] = useState(null);

  // Retrieve the user from sessionStorage, and parse it if it exists
  const storedUser = sessionStorage.getItem('user');
  const user = storedUser ? JSON.parse(storedUser) : null; // Parse the stored string to object if user exists

  useEffect(() => {
    // Fetch movie details using the movieId
    fetch(`http://localhost:5001/movies/${movieId}`)
      .then((response) => response.json())
      .then((data) => setMovie(data))
      .catch((error) => console.error('Error fetching movie:', error));
  }, [movieId]);

  if (!movie) {
    return <p>Loading movie details...</p>;
  }

  return (
    console.log(user.name),
    <div className="book-now-page">
      <h1>Welcome to the Book Now page!</h1>
      {user ? (
        <h3>Hi, {user.name}!</h3> // Display the user's name
      ) : (
        <p>Please log in to book a movie.</p> // Handle case when no user is logged in
      )}
      <p>You are booking tickets for <strong>{movie.movie_name}</strong></p>
      <p>Proceed with your booking here.</p>
    </div>
  );
}

export default BookNow;
