import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom'; // Import Link for routing

function Movie() {
  const [movies, setMovies] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch movie data from the backend
  useEffect(() => {
    fetch('http://localhost:5001/movies')  // Fetch from the backend
      .then((response) => response.json())
      .then((data) => {
        setMovies(data);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching movies:', error);
        setLoading(false);
      });
  }, []);

  if (loading) {
    return <p>Loading movies...</p>;
  }

  return (
    <div className="movie-container">
      {movies.length === 0 ? (
        <p>No movies available.</p>
      ) : (
        movies.map((movie) => (
          <div className="movie-card" key={movie._id}>
            <img src={movie.image} alt={movie.movie_name} className="movie-image" />
            <div className="movie-buttons">
              {/* View Details button */}
              <Link to={`/movie/${movie._id}`} className="btn-view-details">View Details</Link>
              {/* Book Now button */}
              <button className="btn-book-now">Book Now</button>
            </div>
          </div>
        ))
      )}
    </div>
  );
}

export default Movie;
